import React from 'react'

function Rating({value,text,colour}) {
  return (
    <div className='rating'>
      <span>
        <i style={{colour}} className={
            value >= 1?'fas fa-star' //solid star
            :value >= 0.5?'fas fa-star-half-alt':'fas fa-star'
        }></i>
      </span>
    </div>
  )
}

export default Rating
